import React, {useState} from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {

const [title, setTitle] = useState("");
const [amount, setAmount] = useState("");
const [date, setDate] = useState("");

const titleChangeHanlder = (e) => {
   setTitle(e.target.value)

}

const amountChangeHanlder = (e) => {
  setAmount(e.target.value)
  console.log(amount)
}


const dateChangeHanlder = (e) => {
    console.log(setDate(e.target.value))
    console.log(date)
}

const submitHandler = (e) => {
    e.preventDefault()
    const expenseData = {
        title : title,
        amount : amount,
        date : new Date(date)
    }

    console.log(expenseData);
}

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
         <input type="text" onChange={titleChangeHanlder}/>
        </div>

        <div className='new-expense__control'>
          <label>Amount</label>
          <input type='number' min='0.01' step='0.01' onChange={amountChangeHanlder} />
        </div>

        <div className='new-expense__control'>
          <label>Date</label>
          <input type='date' 
             min='2019-01-01'
             max='2022-12-31'
          onChange={dateChangeHanlder}/>
        </div>
      </div>

      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
